svg.script:

// Component data for information display
const componentData = {
    cpu: {
        title: 'CPU - Central Processing Unit',
        description: 'The brain of your computer! This powerful chip processes all instructions and calculations at incredible speeds. Modern CPUs can execute billions of operations per second, handling everything from simple calculations to complex gaming and video processing.'
    },
    gpu: {
        title: 'GPU - Graphics Processing Unit',
        description: 'The graphics powerhouse! This component renders all the beautiful visuals you see on your screen. With thousands of cores working in parallel, it excels at gaming, video editing, and AI computations. Those spinning fans keep it cool during intense workloads.'
    },
    ram: {
        title: 'RAM - Random Access Memory',
        description: 'Your computer\'s short-term memory! These colorful sticks store data that your CPU needs quick access to. More RAM means your computer can multitask better and run more programs simultaneously without slowing down.'
    },
    storage: {
        title: 'Storage Drives',
        description: 'Your computer\'s long-term memory! The thin SSD provides lightning-fast access to your files and programs, while the larger HDD offers massive storage capacity for photos, videos, and documents.'
    },
    motherboard: {
        title: 'Motherboard - The Main Circuit Board',
        description: 'The nervous system of your PC! This green circuit board connects all components together with thousands of tiny gold pathways, allowing them to communicate and work as a unified system. Every component plugs into the motherboard.'
    },
    power: {
        title: 'Power Supply Unit (PSU)',
        description: 'The heart that pumps life into your PC! It converts dangerous wall electricity (120V) into safe, precise voltages (3V, 5V, 12V) that each component needs. The colorful cables deliver power throughout the system.'
    },
    heatsink: {
        title: 'CPU Heatsink & Cooler',
        description: 'The CPU\'s cooling system! The aluminum heatsink with fins draws heat away from the CPU, while the fan blows cool air across it. Without proper cooling, your CPU would overheat and shut down in seconds.'
    },
    fan: {
        title: 'Cooling Fan',
        description: 'Essential for keeping components cool! This fan spins at thousands of RPM, moving air across heatsinks and through the case to prevent overheating. Modern fans are designed to be quiet while moving lots of air.'
    },
    bios: {
        title: 'BIOS Chip',
        description: 'The computer\'s startup instructions! This small chip contains the Basic Input/Output System that tells your computer how to boot up and recognize all the hardware components when you turn it on.'
    },
    battery: {
        title: 'CMOS Battery',
        description: 'Keeps time and settings! This small battery powers the motherboard\'s clock and stores BIOS settings even when your computer is unplugged. It typically lasts 5-10 years before needing replacement.'
    },
    connectors: {
        title: 'I/O Connectors',
        description: 'Your computer\'s connection hub! These ports include USB for devices, audio jacks for headphones and microphones, ethernet for internet, and video outputs like HDMI for monitors. They let you connect the outside world to your PC.'
    }
};

// Function to communicate with parent window
function updateParentInfo(componentType) {
    if (window.parent && window.parent.updateComponentInfoFromSVG) {
        window.parent.updateComponentInfoFromSVG(componentType, componentData[componentType]);
    }
}

// Function to highlight component
function highlightComponent(element) {
    // Remove previous highlights
    const allComponents = document.querySelectorAll('.clickable-component');
    allComponents.forEach(comp => {
        comp.classList.remove('component-highlight');
    });

    // Add highlight to clicked component
    element.classList.add('component-highlight');

    // Remove highlight after 4 seconds
    setTimeout(() => {
        element.classList.remove('component-highlight');
    }, 4000);
}

// Add click handlers to all clickable components
document.addEventListener('DOMContentLoaded', function() {
    const clickableComponents = document.querySelectorAll('.clickable-component');

    clickableComponents.forEach(component => {
        component.addEventListener('click', function(e) {
            e.preventDefault();
            e.stopPropagation();

            const componentType = this.getAttribute('data-component');
            if (componentType && componentData[componentType]) {
                highlightComponent(this);
                updateParentInfo(componentType);
            }
        });

        // Add hover effects
        component.addEventListener('mouseenter', function() {
            this.style.cursor = 'pointer';
            this.style.filter = 'brightness(1.2) drop-shadow(0 0 8px rgba(102, 126, 234, 0.6))';
        });

        component.addEventListener('mouseleave', function() {
            if (!this.classList.contains('component-highlight')) {
                this.style.filter = '';
            }
        });
    });
});
